mapper.select:
select c.course_id,user_id,term_id,course_name,start_week,end_week,course_time_id,weekday,classroom,start_lesson,end_lesson
from course c inner join course_time ct on c.course_id = ct.course_id
where term_id=#{param1}  and  #{param2}>=start_week and #{param2}<=end_week and user_id=#{param3};

select c.course_id,user_id,term_id,course_name,start_week,end_week,course_time_id,weekday,classroom,start_lesson,end_lesson
from course c inner join course_time ct on c.course_id = ct.course_id
where term_id=#{param1}  and  #{param2}>=start_week and #{param2}<=end_week and user_id=#{param3} and weekDay=#{param4};
